import React, { useState } from 'react';
import SignupForm from '../components/Auth/SignupForm';
import { createUserWithEmailAndPassword } from 'firebase/auth';
// import { auth } from '../firebase';
import { register } from '../api';

export default function SignupPage({ onRegisterSuccess }) {
  const [signupError, setSignupError] = useState('');
  const [email, setEmail] = useState('');
  const [password1, setPassword1] = useState('');
  const [password2, setPassword2] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setSignupError('');

    if (password1 !== password2) {
        setSignupError('비밀번호가 일치하지 않아요.');
        return;
    }

    try {
        const res = await register(email, password1, password2);
        localStorage.setItem('access', res.access);
        localStorage.setItem('refresh', res.refresh);
        onRegisterSuccess(); // 회원가입 성공 후 이동
    } catch (err) {
        setSignupError(err.message);
    }
};

    return (
      <form onSubmit={handleRegister}>
        <h2>회원가입</h2>
        {signupError && <div className="error-text">{signupError}</div>}
        <input
          type="email"
          placeholder="이메일"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="비밀번호"
          value={password1}
          onChange={(e) => setPassword1(e.target.value)}
        />
        <input
          type="password"
          placeholder="비밀번호 확인"
          value={password2}
          onChange={(e) => setPassword2(e.target.value)}
        />
        <button type="submit">회원가입</button>
      </form>
    );
  }

